import React, { useEffect, useState } from 'react';
import { useGoogleLogin } from '@react-oauth/google';
import axios from 'axios';
import './LoginPage.css';  // Your CSS file for styles
import logo from './assets/logo.png';  // Your logo image

const LoginPage = () => {
  const [error, setError] = useState(null);
  const [adminEmails, setAdminEmails] = useState([]); // State to store admin emails

  // Function to fetch admin emails from the server
  const fetchAdminEmails = async () => {
    try {
      const response = await axios.get('http://localhost:5000/api/admins');
      setAdminEmails(response.data.map(admin => admin.emailId)); // Store email IDs in state
      console.log("Fetched Admin Emails:", response.data); // Log fetched emails
    } catch (err) {
      console.error('Error fetching admin emails:', err);
      setError('Failed to fetch admin emails');
    }
  };

  useEffect(() => {
    fetchAdminEmails(); // Call the fetch function when the component mounts
  }, []);

  // Hook for Google login
  const googleLogin = useGoogleLogin({
    onSuccess: async tokenResponse => {
      // Existing login logic...
    },
    onError: () => {
      console.log('Login Failed');
      setError('Login Failed');
    },
  });

  // Rest of your existing code...

  return (
    <div className="login-page">
      <div className="login-container">
        <h2 className="welcome-text">Welcome Back!</h2>
        <img
          src={logo}
          alt="Bannari Amman Institute of Technology"
          className="login-logo"
        />
        <hr className="divider" />
        {error && <p>{error}</p>}
        
        {/* Display the fetched admin email IDs */}
        <h3>Admin Email IDs:</h3>
        <ul>
          {adminEmails.map((email, index) => (
            <li key={index}>{email}</li>
          ))}
        </ul>

        <button className="google-sign-in" onClick={handleAdminPageClick}>Admin Sign In</button>
        <p></p>
        <button className="google-sign-in" onClick={handleFacultyPageClick}>Faculty Sign In</button>
        <p className="sign-in-text">Sign in with your BIT account</p>
      </div>
    </div>
  );
};

export default LoginPage;
