import React, { useState } from 'react';
import { useGoogleLogin } from '@react-oauth/google';
import axios from 'axios';
import './LoginPage.css';  // Your CSS file for styles
import logo from './assets/logo.png';  // Your logo image

const LoginPage = () => {
  const [error, setError] = useState(null);

  // Hook for Google login
  const googleLogin = useGoogleLogin({
    onSuccess: async tokenResponse => {
      try {
        // Get user info from Google
        const res = await axios.get('https://www.googleapis.com/oauth2/v3/userinfo', {
          headers: {
            Authorization: `Bearer ${tokenResponse.access_token}`,
          },
        });

        const userInfo = res.data;  // Store user details
        console.log('User info fetched from Google:', userInfo);

        // Send user email to backend for role checking
        const roleResponse = await axios.post('http://localhost:5000/api/checkRoles', { email: userInfo.email });
        console.log('Role data fetched from backend:', roleResponse.data);

        // Redirect based on the role
        if (roleResponse.data.role === 'admin') {
          window.location.href = 'http://localhost:3000/admin/dashboard';  // Redirect to Admin Dashboard
        } else {
          window.location.href = 'http://localhost:3000/faculty/dashboard';  // Redirect to Faculty Dashboard
        }
      } catch (err) {
        console.error('Error during login or role check:', err);
        setError('Failed to fetch user details or check role');
      }
    },
    onError: () => {
      console.log('Google login failed');
      setError('Login Failed');
    },
  });

  const handleAdminPageClick = () => {
    googleLogin(); // Trigger Google login for Admin
  };

  const handleFacultyPageClick = () => {
    googleLogin(); // Trigger Google login for Faculty
  };

  return (
    <div className="login-page">
      <div className="login-container">
        <h2 className="welcome-text">Welcome Back!</h2>
        <img
          src={logo}
          alt="Bannari Amman Institute of Technology"
          className="login-logo"
        />
        <hr className="divider" />
        {error && <p>{error}</p>}
        <button className="google-sign-in" onClick={handleAdminPageClick}>Admin Sign In</button>
        <p></p>
        <button className="google-sign-in" onClick={handleFacultyPageClick}>Faculty Sign In</button>
        <p className="sign-in-text">Sign in with your BIT account</p>
      </div>
    </div>
  );
};

export default LoginPage;
