import React, { useState } from 'react';
import { useGoogleLogin } from '@react-oauth/google';
import axios from 'axios';
import './LoginPage.css';
import logo from './assets/logo.png';

const LoginPage = () => {
  const [error, setError] = useState(null);

  const login = useGoogleLogin({
    onSuccess: async tokenResponse => {
      try {
        const res = await axios.get('https://www.googleapis.com/oauth2/v3/userinfo', {
          headers: {
            Authorization: `Bearer ${tokenResponse.access_token}`
          }
        });

        const userInfo = res.data;

        // Example of sending multiple emails (can be modified to get them dynamically)
        const emails = [userInfo.email, 'faculty@example.com', 'otheradmin@example.com'];

        // Check roles for multiple emails from the backend
        const roleRes = await axios.post('http://localhost:5000/api/checkRoles', {
          emails
        });

        const roles = roleRes.data.roles;

        // Example: Redirect based on the first user's role (you can modify logic for multiple users)
        const currentUserRole = roles[userInfo.email];
        if (currentUserRole === 'admin') {
          window.location.href = 'http://localhost:3000/admin/dashboard';
        } else {
          window.location.href = 'http://localhost:3000/faculty/dashboard';
        }
      } catch (err) {
        console.error('Failed to fetch user info or check role', err);
        setError('Failed to authenticate or check role');
      }
    },
    onError: () => {
      console.log('Login Failed');
      setError('Login Failed');
    },
  });

  const handleSignIn = () => {
    login();
  };

  return (
    <div className="login-page">
      <div className="login-container">
        <h2 className="welcome-text">Welcome Back!</h2>
        <img
          src={logo}
          alt="Bannari Amman Institute of Technology"
          className="login-logo"
        />
        <hr className="divider" />
        {error && <p>{error}</p>}
        <button className="google-sign-in" onClick={handleSignIn}>Sign In with Google</button>
        <p className="sign-in-text">Sign in with your BIT account</p>
      </div>
    </div>
  );
};

export default LoginPage;
