import React, { useState } from 'react';
import { useGoogleLogin } from '@react-oauth/google';
import axios from 'axios';
import './LoginPage.css';  // Create this CSS file for styles
import logo from './assets/logo.png';  // Place your logo image in the src/assets directory

const LoginPage = () => {
  const [error, setError] = useState(null);

  // Function to handle Google login
  const login = useGoogleLogin({
    onSuccess: async (tokenResponse) => {
      try {
        // Fetch user information from Google API
        const res = await axios.get('https://www.googleapis.com/oauth2/v3/userinfo', {
          headers: {
            Authorization: `Bearer ${tokenResponse.access_token}`
          }
        });

        const userInfo = res.data;  // Store user details

        // Check role from the backend
        const roleRes = await axios.post('http://localhost:5000/api/checkRoles', {
          emails: [userInfo.email]  // Pass email array to backend
        });

        const roles = roleRes.data.roles;
        const userRole = roles[userInfo.email];

        // Redirect based on role
        if (userRole === 'admin') {
          window.location.href = 'http://localhost:3000/admin/dashboard'; // Redirect to Admin Dashboard
        } else {
          window.location.href = 'http://localhost:3000/faculty/dashboard'; // Redirect to Faculty Dashboard
        }
      } catch (err) {
        console.error('Failed to fetch user info or check role', err);
        setError('Failed to authenticate or check role');
      }
    },
    onError: () => {
      console.log('Login Failed');
      setError('Login Failed');
    },
  });

  const handleSignIn = () => {
    login();
  };

  return (
    <div className="login-page">
      <div className="login-container">
        <h2 className="welcome-text">Welcome Back!</h2>
        <img
          src={logo}
          alt="Bannari Amman Institute of Technology"
          className="login-logo"
        />
        <hr className="divider" />
        {error && <p>{error}</p>}
        <button className="google-sign-in" onClick={handleSignIn}>
          Sign In with Google
        </button>
        <p className="sign-in-text">Sign in with your BIT account</p>
      </div>
    </div>
  );
};

export default LoginPage;
