import React, { useState } from 'react';
import axios from 'axios';

const LoginPage = () => {
    const [email, setEmail] = useState('');

    const handleSubmit = async (e) => {
        e.preventDefault();
        const emails = [email]; // Assuming you're only logging in a single user

        try {
            // API call to check roles
            const response = await axios.post('http://localhost:5000/api/checkRoles', { emails });
            const { roles } = response.data;

            // Check if email has admin role
            if (roles[email.toLowerCase()] === 'admin') {
                window.location.href = '/admin-dashboard'; // Redirect to admin page
            } else {
                window.location.href = '/faculty-dashboard'; // Redirect to faculty page
            }
        } catch (error) {
            console.error('Failed to fetch user info or check role', error);
        }
    };

    return (
        <div>
            <h2>Login</h2>
            <form onSubmit={handleSubmit}>
                <label>Email: </label>
                <input 
                    type="email" 
                    value={email}
                    onChange={(e) => setEmail(e.target.value)}
                    required
                />
                <button type="submit">Login</button>
            </form>
        </div>
    );
};

export default LoginPage;
