import React, { useState, useEffect } from 'react';
import { useHistory } from 'react-router-dom';
import axios from 'axios';

const LoginPage = () => {
  const [admins, setAdmins] = useState([]);
  const [email, setEmail] = useState('');
  const history = useHistory();

  useEffect(() => {
    const fetchAdmins = async () => {
      try {
        const response = await axios.get('http://localhost:5000/api/admins'); // Adjust the URL as necessary
        setAdmins(response.data); // Assuming the response data is an array of admin objects
      } catch (error) {
        console.error('Error fetching admin emails:', error);
      }
    };

    fetchAdmins();
  }, []);

  const handleLogin = () => {
    // Check if the entered email exists in the admin list
    if (admins.some(admin => admin.emailId === email)) {
      history.push('/admin-dashboard'); // Redirect to admin dashboard
    } else {
      history.push('/faculty-dashboard'); // Redirect to faculty dashboard
    }
  };

  return (
    <div>
      <h1>Login Page</h1>
      <input
        type="email"
        placeholder="Enter your email"
        value={email}
        onChange={(e) => setEmail(e.target.value)}
      />
      <button onClick={handleLogin}>Login</button>

      {/* Render admins if available */}
      {admins.length > 0 ? (
        <div>
          <h2>Admin Emails:</h2>
          {admins.map((admin) => (
            <div key={admin._id}>{admin.emailId}</div>
          ))}
        </div>
      ) : (
        <p>No admins found</p>
      )}
    </div>
  );
};

export default LoginPage;
