import React, { useState } from 'react';
import { useGoogleLogin } from '@react-oauth/google';
import axios from 'axios';
import './LoginPage.css';  // Assuming this is your CSS file for styles
import logo from './assets/logo.png';  // Assuming your logo image

const LoginPage = () => {
  const [error, setError] = useState(null);

  // Function to handle Google login
  const login = (roleType) => useGoogleLogin({
    onSuccess: async tokenResponse => {
      try {
        const res = await axios.get('https://www.googleapis.com/oauth2/v3/userinfo', {
          headers: {
            Authorization: `Bearer ${tokenResponse.access_token}`,
          },
        });

        const userInfo = res.data; // Store user details

        // Check the role from your backend
        const roleCheckResponse = await axios.post('http://localhost:5000/api/checkRoles', { email: userInfo.email });
        const userRole = roleCheckResponse.data.role;

        // Redirect based on user role
        if (userRole === 'admin' && roleType === 'admin') {
          window.location.href = 'http://localhost:3000/admin/dashboard'; // Redirect to Admin Dashboard
        } else if (userRole === 'faculty' && roleType === 'faculty') {
          window.location.href = 'http://localhost:3000/faculty/dashboard'; // Redirect to Faculty Dashboard
        } else {
          setError('Access Denied: You do not have the correct role.');
        }
      } catch (err) {
        console.error('Failed to fetch user info or role', err);
        setError('Failed to fetch user info or check role');
      }
    },
    onError: () => {
      console.log('Login Failed');
      setError('Login Failed');
    },
  })();

  const handleAdminPageClick = () => {
    login('admin'); // Trigger Google login and check for Admin role
  };

  const handleFacultyPageClick = () => {
    login('faculty'); // Trigger Google login and check for Faculty role
  };

  return (
    <div className="login-page">
      <div className="login-container">
        <h2 className="welcome-text">Welcome Back!</h2>
        <img
          src={logo}
          alt="Bannari Amman Institute of Technology"
          className="login-logo"
        />
        <hr className="divider" />
        {error && <p>{error}</p>}
        <button className="google-sign-in" onClick={handleAdminPageClick}>Admin Sign In</button><p></p>
        <button className="google-sign-in" onClick={handleFacultyPageClick}>Faculty Sign In</button>
        <p className="sign-in-text">Sign in with your BIT account</p>
      </div>
    </div>
  );
};

export default LoginPage;
